package curs6;

public class WhileExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=0;i<5;i++) {
			System.out.println(i);
		}
		System.out.println("-----------");
		
		int i=0;
		while(i<5) {
			//i++;
			System.out.println(i);
			i++;
		}
		
		//syntax 
		while(true) {
			//corpul buclei
		}
	}

}
